import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './Pages/Login';
import Register from './Pages/Register';
import Home from './Pages/Home';
import FarmerDashboard from './Pages/FarmerDashbaord';
import Navbar from './components/Navbar';

const App = () => {
    const token = localStorage.getItem('token');  // Get token from localStorage to check if user is authenticated
    const user = JSON.parse(localStorage.getItem('user'));  // Retrieve user data from localStorage

    return (
        <Router>
            <Navbar />
            <Routes>
                {/* Public Routes */}
                <Route path="/login" element={!token ? <Login /> : <Navigate to={user?.isFarmer ? '/Farmer Dashbaord' : '/Home'} />} />
                <Route path="/register" element={!token ? <Register /> : <Navigate to={user?.isFarmer ? '/Farmer Dashbaord' : '/Home'} />} />

                {/* Private Routes */}
                <Route path="/home" element={token && !user?.isFarmer ? <Home /> : <Navigate to="/login" />} />
                <Route path="/farmer-dashboard" element={token && user?.isFarmer ? <FarmerDashboard /> : <Navigate to="/login" />} />

                {/* Default Route */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
