import React, { useState } from 'react';
import '../styles/AddProduct.css';

const AddProduct = () => {
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState(null);

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        const reader = new FileReader();
        reader.onloadend = () => {
            setImage(reader.result); // Store base64 string
        };
        if (file) {
            reader.readAsDataURL(file);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const newProduct = { name, price, description, image, quantity: 1, id: Date.now() };

        // Get cart from localStorage or create an empty cart if it doesn't exist
        let cart = JSON.parse(localStorage.getItem('cart')) || [];

        // Check if product already exists in cart
        const existingProductIndex = cart.findIndex(item => item.id === newProduct.id);
        if (existingProductIndex >= 0) {
            // If product exists, increase quantity
            cart[existingProductIndex].quantity += 1;
        } else {
            // Add new product to cart
            cart.push(newProduct);
        }

        // Save updated cart to localStorage
        localStorage.setItem('cart', JSON.stringify(cart));

        // Debugging: Check cart in localStorage
        console.log('Cart after adding product:', localStorage.getItem('cart'));
        
        alert('Product added to cart successfully');
        
        // Reset form fields
        setName('');
        setPrice('');
        setDescription('');
        setImage(null);
    };

    return (
        <form className="add-product-form" onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Product Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
            />
            <input
                type="number"
                placeholder="Price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                required
            />
            <textarea
                placeholder="Description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
            />
            <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
                required
            />
            <button type="submit">Add Product</button>
        </form>
    );
};

export default AddProduct;
