import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './Pages/Login';
import Register from './Pages/Register';
import Home from './Pages/Home';
import FarmerDashboard from './Pages/FarmerDashbaord'
import Navbar from './components/Navbar';

const App = () => {
    // Check if the user is authenticated and get user info from localStorage
    const storedUser = localStorage.getItem('user');
    const user = storedUser ? JSON.parse(storedUser) : null;
    const isAuthenticated = user !== null;  // Check if user is authenticated

    return (
        <Router>
            <Navbar />
            <Routes>
                {/* Public Routes */}
                <Route
                    path="/login"
                    element={!isAuthenticated ? <Login /> : <Navigate to={user?.isFarmer ? "/farmer-dashboard" : "/home"} />}
                />
                <Route
                    path="/register"
                    element={!isAuthenticated ? <Register /> : <Navigate to={user?.isFarmer ? "/farmer-dashboard" : "/home"} />}
                />

                {/* Private Routes */}
                <Route
                    path="/home"
                    element={isAuthenticated && !user?.isFarmer ? <Home /> : <Navigate to="/login" />}
                />
                <Route
                    path="/farmer-dashboard"
                    element={isAuthenticated && user?.isFarmer ? <FarmerDashboard /> : <Navigate to="/login" />}
                />

                {/* Default Route */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
