import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './Pages/Login';
import Register from './Pages/Register';
import Home from './Pages/Home';
import FarmerDashboard from './Pages/FarmerDashboard';
import Navbar from './components/Navbar';

const App = () => {
    const [user, setUser] = useState(null);  // State to store user data
    const [loading, setLoading] = useState(true);  // Loading state to handle async user data loading

    // Check if the user is authenticated when the app loads
    useEffect(() => {
        const storedToken = localStorage.getItem('token');
        const storedUser = localStorage.getItem('user');

        // Simulate async loading of user data
        setTimeout(() => {
            if (storedToken && storedUser) {
                setUser(JSON.parse(storedUser));  // Set user data if available
            }
            setLoading(false);  // Set loading to false once the data is loaded
        }, 1000);  // Simulated delay (adjust as necessary)
    }, []);

    if (loading) {
        return <div>Loading...</div>;  // Display loading message or spinner
    }

    // Check if the user is authenticated
    const isAuthenticated = user !== null;

    return (
        <Router>
            <Navbar />
            <Routes>
                {/* Public Routes */}
                <Route
                    path="/login"
                    element={!isAuthenticated ? <Login /> : user?.isFarmer ? <Navigate to="/farmer-dashboard" /> : <Navigate to="/home" />}
                />
                <Route
                    path="/register"
                    element={!isAuthenticated ? <Register /> : user?.isFarmer ? <Navigate to="/farmer-dashboard" /> : <Navigate to="/home" />}
                />

                {/* Private Routes */}
                <Route
                    path="/home"
                    element={isAuthenticated && !user?.isFarmer ? <Home /> : <Navigate to="/login" />}
                />
                <Route
                    path="/farmer-dashboard"
                    element={isAuthenticated && user?.isFarmer ? <FarmerDashboard /> : <Navigate to="/login" />}
                />

                {/* Default Route */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
