import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './Pages/Login';
import Register from './Pages/Register';
import Home from './Pages/Home';
import FarmerDashboard from './Pages/FarmerDashboard';
import Navbar from './components/Navbar';

const App = () => {
    const isAuthenticated = !!localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user')); // Retrieve user info from localStorage

    return (
        <Router>
            <Navbar />
            <Routes>
                {/* Public Routes */}
                <Route
                    path="/login"
                    element={!isAuthenticated ? <Login /> : user?.isFarmer ? <Navigate to="/farmer-dashboard" /> : <Navigate to="/home" />}
                />
                <Route
                    path="/register"
                    element={!isAuthenticated ? <Register /> : user?.isFarmer ? <Navigate to="/farmer-dashboard" /> : <Navigate to="/home" />}
                />

                {/* Private Routes */}
                <Route
                    path="/home"
                    element={isAuthenticated && !user?.isFarmer ? <Home /> : <Navigate to="/login" />}
                />
                <Route
                    path="/farmer-dashboard"
                    element={isAuthenticated && user?.isFarmer ? <FarmerDashboard /> : <Navigate to="/login" />}
                />

                {/* Default Route */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
