import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './Pages/Login';
import Register from './Pages/Register';
import Home from './Pages/Home';
import FarmerDashboard from './Pages/FarmerDashboard';
import Navbar from './components/Navbar';

const App = () => {
    return (
        <Router>
            <Navbar />
            <Routes>
                {/* Public Routes */}
                <Route
                    path="/login"
                    element={<Login />} // Always route to login if not authenticated
                />
                <Route
                    path="/register"
                    element={<Register />} // Register route
                />

                {/* Redirect to home if login is successful */}
                <Route
                    path="/"
                    element={<Navigate to="/login" />} // Initially route to login
                />

                {/* Private Routes */}
                <Route
                    path="/home"
                    element={
                        // Check if user is logged in, if not redirect to login
                        localStorage.getItem('token') ? <Home /> : <Navigate to="/login" />
                    }
                />
                <Route
                    path="/farmer-dashboard"
                    element={
                        // Check if user is a farmer and logged in
                        localStorage.getItem('token') ? (
                            JSON.parse(localStorage.getItem('user')).isFarmer ? (
                                <FarmerDashboard />
                            ) : (
                                <Navigate to="/home" />
                            )
                        ) : (
                            <Navigate to="/login" />
                        )
                    }
                />

                {/* Default Route */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
